package c1;

public class whiletrue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 0;
		int i =5;

		while(true) {
						
			while(a <=i) {
				a++;
				i--;
				System.out.print("*");
			
                if(a >=i) {
                	break;	
                }
		     
			}		
			 
		}
		
		

	}

}
